export const SET_CONTACTOS = 'SET_CONTACTOS';
export const ADD_CONTACTOS = 'ADD_CONTACTOS';
export const UPDATE_CONTACTOS = 'UPDATE_CONTACTOS';
export const DELETE_CONTACTOS = 'DELETE_CONTACTOS';

export function setContactos(contactos){
    return {
        type: SET_CONTACTOS,
        contactos

    }
}

export function addContactos(contactos){
    return {
        type: ADD_CONTACTOS,
        contactos
    }

}

export function updContactos(contactos){
    return {
        type: UPDATE_CONTACTOS,
        contactos
    }
}

export function delContactos(contactos){
    return {
        type: DELETE_CONTACTOS,
        contactos
    }
}
function handleResponse(response){
    if (response.ok){
        alert(response.json());
        return response.json();
      
    }else{
        alert('hubo un error');
        let error = new Error(response.statusText);
        error.response = response;
        throw error;
    }
}
export function fetchContactos()
{
alert('entro');
    return dispatch =>{
        fetch('https://localhost:44386/api/Contactos')
        .then(handleResponse)
        .then(data => dispatch(
            setContactos(data)
            )).catch(function(err) {
               alert('hubo un error');
            });
    }
    
}
export function saveContactos(data){
    return dispatch =>{
        return fetch('https://localhost:44386/api/Contactos', {
            method: 'post',
            body: JSON.stringify(data),
            headers:{
                "content-Type":"application/json"
            }
        })
        .then(handleResponse)
        .then(data => dispatch(
            addContactos(data)
        ));
    }
}
export function updateContactos(data){
    return dispatch =>{
        return fetch('https://localhost:44386/api/Contactos', {
            method: 'put',
            body: JSON.stringify(data.Id, data)
        })
        .then(handleResponse)
        .then(data => dispatch(
            updContactos(data)
        ));
    }
}

export function deleteContactos(Id){
    return dispatch =>{
        return fetch('https://localhost:44386/api/Contactos', {
            method: 'delete',
            body: JSON.stringify(Id)
        })
        .then(handleResponse)
        .then(data => dispatch(
            delContactos(data)
        ));
    }
}
